import React from 'react';
import ReactDOM from 'react-dom';
import { BaseImageResource } from '@pixi/core';
import App from './App';
import * as PIXI from 'pixi.js';
import * as io from 'socket.io-client';
import * as serviceWorker from './serviceWorker';

const socket = io.connect('wss://wrongway-racer-api.spls.ae/', { transports: ['websocket'] });

socket.on('connect', () => {
  console.log('Connected to server');
});

socket.on('message', (data) => {
  console.log(`Received message: ${data}`);
});

socket.on('disconnect', () => {
  console.log('Disconnected from server');
});

socket.emit('chat message', 'Hello World');

const app = new PIXI.Application({
  width: 800,
  height: 600,
  backgroundColor: 0x1099bb,
});

document.getElementById('game-canvas').appendChild(app.view);

const playerSprite = PIXI.Sprite.from('/player.png');
playerSprite.anchor.set(0.5);
playerSprite.x = 400;
playerSprite.y = 550;
app.stage.addChild(playerSprite);

const enemySprites = {};

function updateEnemies(enemies) {
  Object.keys(enemies).forEach((id) => {
    if (!enemySprites[id]) {
      const enemySprite = PIXI.Sprite.from('/enemy.png');
      enemySprite.anchor.set(0.5);
      app.stage.addChild(enemySprite);
      enemySprites[id] = enemySprite;
    }

    const enemy = enemies[id];
    const enemySprite = enemySprites[id];
    enemySprite.x = enemy.x;
    enemySprite.y = enemy.y;
  });
}

// Listen for socket events and update the sprites accordingly

window.addEventListener('keydown', (event) => {
  if (event.keyCode === 37) {
    // left arrow key
    playerSprite.x -= 5;
  } else if (event.keyCode === 39) {
    // right arrow key
    playerSprite.x += 5;
  }
});
